function animationProduct(obj){
    var cart,top_cart, screen, width_wrapper, imgtodrag, top;
    screen = jQuery(window).width();
    width_wrapper = jQuery('.wrapper').width();
    if (screen <= 992) {
        cart = jQuery('#open-minicart-mobile .icon');
        top_cart = cart.position().top + 70;
    }else{
        cart = jQuery('#open-popup .icon');
        top_cart = cart.position().top + 40;
    }
    imgtodrag = ((jQuery(obj).parent('.actions')).parent('.product-info'));
    if (imgtodrag.length) {
        imgtodrag = ((jQuery(obj).parent('.actions')).parent('.product-info')).parent('.item').find(".product-head img").eq(0);
        top = imgtodrag.offset().top + 100;
    }else{
        imgtodrag = ((jQuery(obj).parent('.actions')).parent('.row')).parent('.col-md-6').find(".delivery-product img").eq(0);
        top = imgtodrag.offset().top;
    }
    if (imgtodrag) {
        if (imgtodrag.length) {
            var imgclone = imgtodrag.clone()
                    .offset({
                        top: top,
                        left: imgtodrag.offset().left
                    })
                    .css({
                        'opacity': '0.5',
                        'position': 'absolute',
                        'height': '150px',
                        'width': '150px',
                        'z-index': '99999999'
                    })
                    .appendTo(jQuery('.wrapper'))
                    .animate({
                        'top': top_cart,
                        'left': cart.position().left + width_wrapper - 60,
                        'width': 75,
                        'height': 75
                    }, 1000, 'easeInOutExpo');

            setTimeout(function () {
                cart.effect("shake", {
                    times: 2
                }, 200);
            }, 1500);

            imgclone.animate({
                'width': 0,
                'height': 0
            }, function () {
                jQuery(this).detach()
            });
        }
    }
}
jQuery(document).ready(function () {
    jQuery('.actions .btn-cart').on('click', function () {
        if(jQuery(this).hasClass('btn-simple') && !jQuery(this).hasClass('BIC')){
            animationProduct(this);
        }
    });
});
