function Calculation(obj, obj_change) {
    var newVal;
    var $button = jQuery(obj);
    var input = jQuery(obj_change);
    var oldValue = input.val();
    var total_quote_price = jQuery('#total_quote_price').val();
    var $price_prd = jQuery(".price-box span.price").first().text().replace('', '');
    var max_price = 500000;
    
    if(parseInt(jQuery('#store-view-id').val()) === 1) {
        $price_prd = $price_prd.replace(",00", "");
        $price_prd = $price_prd.replace(".", "");
    } else {
        $price_prd = $price_prd.replace(".00", "");
        $price_prd = $price_prd.replace(",", "");
    }

    if ($button.hasClass('increase')) {
        newVal = parseFloat(oldValue) + 1;
    } else {
        // Don't allow decrementing below zero
        if (oldValue > 0) {
            newVal = parseFloat(oldValue) - 1;
        } else {
            newVal = 0;
        }
    }
    
    //set qty min = 1
    if(newVal == 0) newVal = 1;

    input.val(newVal, this);
    var div = jQuery('.notification');
//    validateInputQty(newVal, div);


    if(input.attr('id') == 'qty-piquant'){
        var qtyValue = parseInt(jQuery('#qty').val(), 10);
        validateInputQty(qtyValue, div, newVal, input);
    }else{
        var qtyPiquantValue = parseInt(jQuery('#qty-piquant').val(), 10);
        validateInputQty(newVal, div, qtyPiquantValue, input);
    }
}
function validateInputQty(qty, obj1, qty_piquant, obj2) {
    var btnChange = jQuery('.add-to-cart-buttons .btn-cart');
    if(obj2.attr('id') == 'qty-piquant'){
        if (qty_piquant > qty) {
            btnChange.prop('disabled', true);
            jQuery(obj1).html(Translator.translate('The amount of spicy must be less than the number of purchases!'));
        }
    }else{
        if(!isNaN(qty_piquant)){
            if (qty < qty_piquant) {
                btnChange.prop('disabled', true);
                jQuery(obj1).html(Translator.translate('The amount of spicy must be less than the number of purchases!'));
            }
        }
    }
}
